import React from 'react';
import styled from 'styled-components';
import Illustration from './Illustration';

const TallContainer = styled.div`
  height: 55vh;
  display: block;
`;

const AboutPanelVerticalSeparator = styled.div`
  padding: 30px 0px;
`;

const AboutPanelContainer = styled.div`
  display: flex;
  flex-wrap: wrap;
  -webkit-box-pack: justify;
  justify-content: space-between;
  -webkit-box-align: center;
  margin: 0px auto;
  max-width: 1000px;

  @media (max-width: 48em) {
    display: block;
    max-width: 500px;
  }
`;

const AboutPanelTextContainer = styled.div`
  flex: 1;
  max-width: 450px;
`;

const AboutPanelTitle = styled.p`
  font-size: 20px;
  margin-top: 16px;
  font-weight: 400;
`;

const AboutPanelSecondaryText = styled.p`
  font-size: 20px;
  margin-top: 20px;
  color: #666666;
`;

export default function SecondPanel() {
  return (
    <TallContainer>
      <AboutPanelVerticalSeparator />
      <AboutPanelContainer>
        <AboutPanelTextContainer>
          <AboutPanelTitle>Nice to meet you</AboutPanelTitle>
          <AboutPanelSecondaryText>
            Hello! Im Noah, a computer science student based in Gainesville, FL
            who loves building things that live on the internet. I develop
            exceptional websites and web-apps with modern intuitive user
            interfaces and performant modern backends.
          </AboutPanelSecondaryText>
        </AboutPanelTextContainer>
        <Illustration />
      </AboutPanelContainer>
    </TallContainer>
  );
}
